import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import './i18n';
import LoanCalculator from './components/LoanCalculator';
import LanguageSwitcher from './components/LanguageSwitcher';
import StarryBackground from './components/StarryBackground';
import { LoanResult } from './utils/loanCalculator';

const App: React.FC = () => {
  const { t, i18n } = useTranslation();
  const [result, setResult] = useState<LoanResult | null>(null);

  const handleCalculate = (result: LoanResult) => {
    setResult(result);
  };

  return (
    <div className="app" dir={i18n.language === 'ar' ? 'rtl' : 'ltr'}>
      <StarryBackground />
      <header>
        <h1>{t('appTitle')}</h1>
        <LanguageSwitcher />
      </header>
      
      <main>
        <LoanCalculator onCalculate={handleCalculate} />
        
        {result && (
          <div className="result">
            <h2>{t('result')}</h2>
            <div className="result-item">
              <span>{t('maxLoanAmount')}:</span>
              <span>AED {result.maxLoanAmount.toLocaleString()}</span>
            </div>
            <div className="result-item">
              <span>{t('maxLoanTerm')}:</span>
              <span>{result.maxLoanTerm} {t('years')}</span>
            </div>
            <div className="result-item">
              <span>{t('monthlyPayment')}:</span>
              <span>AED {result.monthlyPayment.toLocaleString()}</span>
            </div>
            <div className="fees">
              <h3>{t('fees')}</h3>
              <div className="result-item">
                <span>{t('evaluationFee')}:</span>
                <span>AED {result.fees.evaluation.toLocaleString()}</span>
              </div>
              <div className="result-item">
                <span>{t('registrationFee')}:</span>
                <span>AED {result.fees.registration.toLocaleString()}</span>
              </div>
              <div className="result-item">
                <span>{t('transferFee')}:</span>
                <span>AED {result.fees.transfer.toLocaleString()}</span>
              </div>
              <div className="result-item">
                <span>{t('bankFee')}:</span>
                <span>AED {result.fees.bankFee.toLocaleString()}</span>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default App;
